const Dashboard = () => {
    const [showLogoutModal, setShowLogoutModal] = useState(false);
    const [isCollapsed, setIsCollapsed] = useState(false); // State for sidebar collapse
    const navigate = useNavigate();

    const handleLogout = () => {
      setShowLogoutModal(true);
    };

    const confirmLogout = () => {
      navigate('/'); // Redirect to home
    };

    const toggleSidebar = () => {
      setIsCollapsed(!isCollapsed);
    };

    return (
      <div style={styles.dashboardPage}>
        {/* Sidebar */}
        <nav
          style={{
            ...styles.sidebar,
            ...(isCollapsed && styles.sidebarCollapsed),
          }}
        >
          <div style={{ display: 'flex', alignItems: 'center' }}>
            <img
              src={GreenTrendz}
              alt="Logo"
              width="50"
              height="50"
              className="d-inline-block align-top me-2"
            />
            <h5
              style={{
                ...styles.logo,
                ...(isCollapsed && styles.logoCollapsed),
              }}
            >
              GreenTrendz
            </h5>
          </div>

          {/* Toggle Button */}
          <button style={styles.toggleButton} onClick={toggleSidebar}>
            {isCollapsed ? '»' : '«'}
          </button>

          <ul style={styles.navList}>
            {menuItems.map((item) => (
              <li key={item.path}>
                <a
                  href={item.path}
                  style={{
                    ...styles.navItem,
                    ...(isCollapsed && styles.navItemCollapsed),
                  }}
                  onMouseEnter={(e) => {
                    e.target.style.backgroundColor = '#3b6b5c';
                    e.target.style.transform = 'scale(1.05)';
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.backgroundColor = '';
                    e.target.style.transform = '';
                  }}
                >
                  <item.icon
                    style={{
                      ...styles.navIcon,
                      ...(isCollapsed && styles.navIconCollapsed),
                    }}
                    size={20}
                  />
                  <span
                    style={{
                      ...styles.navLabel,
                      ...(isCollapsed && styles.navLabelCollapsed),
                    }}
                  >
                    {item.label}
                  </span>
                </a>
              </li>
            ))}
          </ul>

          <div
            style={{
              marginTop: 'auto',
              width: '100%',
              display: 'flex',
              justifyContent: 'center',
              padding: '0 20px 20px',
            }}
          >
            <button
              style={{
                ...styles.logoutButton,
                ...(isCollapsed && styles.logoutButtonCollapsed),
              }}
              onClick={handleLogout}
            >
              <Settings size={18} />
              {!isCollapsed && 'Logout'}
            </button>
          </div>
        </nav>

        {/* Logout Confirmation Modal */}
        {showLogoutModal && (
          <div style={styles.modalOverlay}>
            <div style={styles.modalContent}>
              <h3>Confirm Logout</h3>
              <p>Are you sure you want to logout?</p>
              <div style={styles.modalButtons}>
                <button onClick={() => setShowLogoutModal(false)}>Cancel</button>
                <button onClick={confirmLogout}>Yes, Logout</button>
              </div>
            </div>
          </div>
        )}
      </div>
    );
  };
