import React, { useState } from 'react';
import GreenTrendz from '../assets/img/logo_1.png';
import './style.css';  // If style.css is in the same folder as FileUploads.js
import { Info } from 'lucide-react';


import {
    Home,
    LayoutDashboard,
    FileText,
    BarChart2,
    Calculator,
    Settings,
    UserCircle,
    HelpCircle,
    CloudUpload,
} from 'lucide-react';

const styles = {
    landingPage: {
        display: 'flex',
        height: '100vh',
        backgroundColor: '#f4f4f9',
        fontFamily: 'Arial, sans-serif',
      },
      sidebar: {
        width: '250px',
        background: 'linear-gradient(to bottom right, #064e3b, #276749)',
        color: 'white',
        padding: '20px',
        boxSizing: 'border-box',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        position: 'fixed',
        height: '100vh',
        zIndex: 10,
        boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
        transition: 'width 0.3s ease',
      },
      logo: {
        fontSize: '25px',
        marginBottom: '30px',
        fontWeight: 'bold',
        color: '#fff',
        textAlign: 'center',
        marginTop: '20px',
        transition: 'transform 0.3s ease',
      },
      logoHover: {
        transform: 'scale(1.1)',
      },
      navList: {
        listStyle: 'none',
        padding: 0,
        margin: 0,
        width: '100%',
      },
      navItem: {
        color: 'white',
        textDecoration: 'none',
        padding: '15px 20px',
        display: 'flex',
        alignItems: 'center',
        width: '100%',
        borderRadius: '8px',
        marginBottom: '12px',
        transition: 'background-color 0.3s ease, transform 0.3s ease',
        cursor: 'pointer',
        fontfamily: "'Poppins', sans-serif", // Custom font for links
            fontWeight: '500', // Optional for bold text
        },
          navItemHover: {
        backgroundColor: '#3b6b5c',
        transform: 'scale(1.05)',
      },
      navIcon: {
        marginRight: '20px',
        transition: 'transform 0.3s ease',
      },

        content: {
            flex: 1,
            padding: '30px',
            boxSizing: 'border-box',
            backgroundColor: '#f8f9fa', // Light gray background for a softer look
            overflowY: 'auto',
            marginLeft: '250px',
            transition: 'margin-left 0.3s ease',
        },





    button: {
        backgroundColor: '#ffffff', // Default background color
        color: 'Black', // Text color
        border: 'none', // No border
        padding: '10px 20px', // Padding for a nice clickable area
        borderRadius: '5px', // Rounded corners
        fontWeight: 'bold', // Bold text
        fontSize: '16px', // Font size
        cursor: 'pointer', // Pointer cursor on hover
        boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)', // Subtle shadow for a 3D effect
        transition: 'background-color 0.3s ease, transform 0.2s ease', // Smooth transition for hover effects
      },
      infoIcon: {
        marginLeft: '8px',
        cursor: 'pointer',
        fontSize: '6px',
        color: '#666',
        lineHeight: '1',
        width: '20px', // Adjust width to control circle size
    height: '20px', // Adjust height to control circle size
    display: 'inline-flex', // Ensures proper centering
    alignItems: 'center', // Vertically centers the icon within the circle
    justifyContent: 'center', // Horizontally centers the icon within the circle
    borderRadius: '50%', // Ensures it's a circle
    padding: '2px', // Adjust padding for inner spacing
    },
    tooltip: {
        position: 'absolute',
        backgroundColor: '#333',
        color: '#fff',
        padding: '5px 10px',
        borderRadius: '4px',
        fontSize: '12px',
        zIndex: '1000',
        visibility: 'hidden',
        opacity: 0,
        transition: 'opacity 0.3s',
    },
    tooltipVisible: {
        visibility: 'visible',
        opacity: 1,
    },
};

const menuItems = [
    { icon: Home, label: 'Home', path: '/land' },
    { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
    { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
    { icon: FileText, label: 'Reports', path: '/reports' },
    { icon: BarChart2, label: 'Comparison', path: '/comparison' },
    { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
    { icon: Settings, label: 'Settings', path: '/settings' },
    { icon: HelpCircle, label: 'Help & Support', path: '/help' },
    { icon: UserCircle, label: 'Account', path: '/account' }, // Added Account item

];

const Sidebar = () => {
    const [showLogoutPopup, setShowLogoutPopup] = useState(false);

    const [tooltipVisible, setTooltipVisible] = useState({});

    const showTooltip = (key) => {
        setTooltipVisible((prev) => ({ ...prev, [key]: true }));
    };

    const hideTooltip = (key) => {
        setTooltipVisible((prev) => ({ ...prev, [key]: false }));
    };
    const [environmentalData, setEnvironmentalData] = useState({
        renewableEnergyUsage: '',
        totalEnergyConsumption: '',
        greenBuildingCertifications: '',
        ghgEmissionTargets: '',
        energyEfficientSystems: '',

        wastewaterReused: '',
        rainwaterHarvesting: '',
        recycledWaterUse: '',

        zeroWasteToLandfill: '',
        wasteSentToRecyclers: '',

        nativeLandscaping: '',
        biodiversityMeasures: '',
    });

    const [socialData, setSocialData] = useState({
        genderDiversity: '',
        employeeTrainingHours: '',
        volunteerismHoursPerEmployee: '',
        workforceSafetyIncidents: '',
        csrInitiatives: '',
        communityEngagementHours: '',
        sustainableCommunityDevelopment: '',
        humanRightsComplaints: '',
        wellCertifiedProjects: '',
        deiPrograms: '',
    });

    const [governanceData, setGovernanceData] = useState({
        independentDirectors: '',
        boardGovernanceInitiatives: '',
        regulatoryComplianceStatus: '',
        nonComplianceCases: '',
        employeesTrainedOnCodeOfConduct: '',
        ethicsViolationsReported: '',
        riskManagementFramework: '',
        riskAssessmentsConducted: '',
    });

    const [dataImportData, setDataImportData] = useState({
        startDate: '',
        endDate: ''
    });

    const [csvFile, setCsvFile] = useState(null);
    const [logoFile, setLogoFile] = useState(null);

    const [loading, setLoading] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleCsvUpload = (e) => {
        const file = e.target.files[0];
        if (file && file.type !== 'text/csv') {
            alert('Please upload a valid CSV file.');
            return;
        }
        setCsvFile(file);

        // Parse the CSV file
        const reader = new FileReader();
        reader.onload = (event) => {
            const text = event.target.result;
            const rows = text.split('\n').map(row => row.split(',')); // Split CSV into rows and columns
            const headers = rows[0]; // First row is headers
            const data = rows.slice(1); // Rest are data rows

            // Map CSV data to state
            const mappedData = {};
            data.forEach(row => {
                const metric = row[0].trim(); // Metric name
                const value = row[1].trim(); // Metric value
                mappedData[metric] = value;
            });

            // Update state with mapped data
            setEnvironmentalData((prev) => ({ ...prev, ...mappedData }));
            setSocialData((prev) => ({ ...prev, ...mappedData }));
            setGovernanceData((prev) => ({ ...prev, ...mappedData }));
        };
        reader.readAsText(file);
    };

    const handleLogoUpload = (e) => {
        const file = e.target.files[0];
        if (file && !['image/png', 'image/jpeg'].includes(file.type)) {
            alert('Please upload a valid image file (PNG or JPEG).');
            return;
        }
        setLogoFile(file);
    };

    const handleSubmit = async () => {
        // Validation: Check if start and end date are provided when uploading a CSV
        if (!csvFile && (!dataImportData.startDate || !dataImportData.endDate)) {
            alert('Please select both start and end dates if uploading a CSV file.');
            return;
        }

        if (!csvFile || !logoFile) {
            alert('Please upload both CSV and Logo files.');
            return;
        }

        setLoading(true);
        setSuccessMessage('');
        setErrorMessage('');

        const userId = localStorage.getItem('userId'); // Assuming userId is stored in localStorage after login

        if (!userId) {
            alert('User not authenticated. Please log in.');
            return;
        }
        const formData = new FormData();
        formData.append('userId', userId);
        formData.append('environmentalData', JSON.stringify(environmentalData));
        formData.append('socialData', JSON.stringify(socialData));
        formData.append('governanceData', JSON.stringify(governanceData));
        formData.append('startDate', dataImportData.startDate);
        formData.append('endDate', dataImportData.endDate);

        if (csvFile) formData.append('csvFile', csvFile);
        if (logoFile) formData.append('logoFile', logoFile);
   // Debugging: Log form data for inspection
   console.log('Submitting Form Data:');
   console.log('Environmental Data:', environmentalData);
   console.log('Social Data:', socialData);
   console.log('Governance Data:', governanceData);
   console.log('Start Date:', dataImportData.startDate);
   console.log('End Date:', dataImportData.endDate);

        try {
            const response = await fetch('http://localhost:5003/api/save-esg-data', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                const result = await response.json();
                setSuccessMessage(result.message || 'Data submitted successfully!');
            } else {
                const errorDetail = await response.text();
                throw new Error(`Error ${response.status}: ${errorDetail}`);
            }
        } catch (error) {
            setErrorMessage('Error submitting data: ' + error.message);
        } finally {
            setLoading(false);
        }
    };

