import React from 'react';

import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  HelpCircle,
  ArrowRight,
  ShieldCheck,
  BarChart,
  Globe,
  CloudUpload,
  Trophy,  // Added new icon for benchmarking
} from 'lucide-react';

const styles = {
  landingPage: {
    display: 'flex',
    height: '100vh',
    backgroundColor: '#f4f4f9',
    fontFamily: 'Arial, sans-serif',
  },
  sidebar: {
    width: '250px',
    background: 'linear-gradient(to bottom right, #064e3b, #276749)',
    color: 'white',
    padding: '20px',
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    position: 'fixed',
    height: '100vh',
    zIndex: 10,
    boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
    transition: 'width 0.3s ease',
  },
  logo: {
    fontSize: '30px',
    marginBottom: '30px',
    fontWeight: 'bold',
    color: '#fff',
    textAlign: 'center',
    marginTop: '20px',
    transition: 'transform 0.3s ease',
  },
  logoHover: {
    transform: 'scale(1.1)',
  },
  navList: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
    width: '100%',
  },
  navItem: {
    color: 'white',
    textDecoration: 'none',
    padding: '15px 20px',
    display: 'flex',
    alignItems: 'center',
    width: '100%',
    borderRadius: '8px',
    marginBottom: '12px',
    transition: 'background-color 0.3s ease, transform 0.3s ease',
    cursor: 'pointer',
  },
  navItemHover: {
    backgroundColor: '#3b6b5c',
    transform: 'scale(1.05)',
  },
  navIcon: {
    marginRight: '20px',
    transition: 'transform 0.3s ease',
  },
  content: {
    flex: 1,
    padding: '30px',
    boxSizing: 'border-box',
    backgroundColor: '#fffff',
    overflowY: 'auto',
    marginLeft: '250px',
    transition: 'margin-left 0.3s ease',
  },

  mainSection: {
    padding: '30px',
    backgroundColor: '#f9f9f9',
    borderRadius: '12px',
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
    marginBottom: '30px',
    transition: 'box-shadow 0.3s ease',
  },
  button: {
    backgroundColor: '#fff',
    color: '#276749',
    padding: '12px 25px',
    borderRadius: '8px',
    border: 'none',
    fontWeight: 'bold',
    display: 'flex',
    alignItems: 'center',
    gap: '10px',
    transition: 'transform 0.3s ease, background-color 0.3s ease, box-shadow 0.3s ease',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
  },
  buttonHover: {
    transform: 'scale(1.05)',
    backgroundColor: '#0b4429',
    color: '#fff',
    boxShadow: '0 6px 15px rgba(0, 0, 0, 0.2)',
  },
  buttonOutline: {
    backgroundColor: 'transparent',
    color: '#fff',
    padding: '12px 25px',
    borderRadius: '8px',
    border: '1px solid #fff',
    fontWeight: 'bold',
    transition: 'background-color 0.3s ease, transform 0.3s ease, box-shadow 0.3s ease',
  },
  buttonOutlineHover: {
    backgroundColor: '#fff',
    color: '#276749',
    transform: 'scale(1.05)',
    boxShadow: '0 6px 15px rgba(0, 0, 0, 0.1)',
  },
};

// Updated Features Data
const features = [
  {
    icon: FileText,
    title: 'Automated Reporting',
    description: 'Generate comprehensive ESG reports with just a few clicks. Save time and reduce errors.',
  },
  {
    icon: BarChart2,
    title: 'Data Analytics',
    description: 'Powerful analytics tools to track and visualize your sustainability metrics.',
  },
  {
    icon: Calculator,
    title: 'Carbon Calculator',
    description: 'Accurate carbon footprint calculations based on industry standards.',
  },
  {
    icon: Trophy,
    title: 'Benchmarking',
    description: 'Compare your ESG performance against industry peers and standards.',
  },
];

// Stats Data
const stats = [
  { value: '2M+', label: 'Carbon Data Points' },
  { value: '500+', label: 'Companies' },
  { value: '98%', label: 'Accuracy Rate' },
  { value: '24/7', label: 'Support' },
];

const menuItems = [
  { icon: Home, label: 'Home', path: '/land' },
  { icon: CloudUpload, label: 'Data Upload', path: '/data-upload' },
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
];

const LandingPage = () => {
  return (
    <div style={styles.landingPage}>
      {/* Sidebar */}
      <nav style={styles.sidebar}>
        <h2
          style={styles.logo}
          onMouseEnter={(e) => e.target.style.transform = 'scale(1.1)'}
          onMouseLeave={(e) => e.target.style.transform = ''}
        >
          Platform Logo
        </h2>
        <ul style={styles.navList}>
          {menuItems.map((item) => (
            <li key={item.path}>
              <a
                href={item.path}
                style={styles.navItem}
                onMouseEnter={(e) => {
                  e.target.style.backgroundColor = '#3b6b5c'; // Hover effect color
                  e.target.style.transform = 'scale(1.05)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.backgroundColor = '';
                  e.target.style.transform = '';
                }}
              >
                <item.icon style={styles.navIcon} size={20} />
                <span>{item.label}</span>
              </a>
            </li>
          ))}
        </ul>
      </nav>

      {/* Main Content */}
      <main style={styles.content}>
        {/* Hero Section */}
        <div
          style={{
            color: 'white',
            padding: '50px',
            marginBottom: '30px',
            textAlign: 'center',
            transition: 'transform 0.3s ease',
          }}
        >
          <img
            src="https://via.placeholder.com/1500x500" // Replace with your desired image URL
            alt="Hero Background"
            style={{
              width: '100%',
              height: 'auto',
              marginBottom: '30px',
              objectFit: 'cover',
            }}
          />
          <h1 style={{ fontSize: '42px', fontWeight: 'bold', marginBottom: '20px' }}>
            Transform Your ESG Reporting Process
          </h1>
          <p style={{ fontSize: '20px', marginBottom: '30px' }}>
            Generate comprehensive ESG reports, track sustainability metrics, and make data-driven decisions with our powerful platform.
          </p>
          <div style={{ display: 'flex', gap: '15px', justifyContent: 'center' }}>
            <button style={{ ...styles.button, ...styles.buttonHover }}>
              Get Started <ArrowRight size={20} />
            </button>
            <button style={{ ...styles.buttonOutline, ...styles.buttonOutlineHover }}>
              Watch Demo
            </button>
          </div>
        </div>

        {/* Features Section */}
        <section style={{ padding: '40px', backgroundColor: '#f9f9f9' }}>
          <h2 style={{ fontSize: '30px', fontWeight: 'bold', marginBottom: '30px', textAlign: 'center' }}>
            Key Features
          </h2>
          <div style={{ display: 'flex', gap: '20px', justifyContent: 'space-between', flexWrap: 'wrap' }}>
            {features.map((feature, index) => (
              <div
                key={index}
                style={{
                  flex: '1 1 calc(25% - 20px)',
                  backgroundColor: '#ffffff',
                  padding: '20px',
                  borderRadius: '12px',
                  boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
                  textAlign: 'center',
                  transition: 'box-shadow 0.3s ease',
                }}
                onMouseEnter={(e) => {
                  e.target.style.boxShadow = '0 6px 20px rgba(0,0,0,0.2)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.boxShadow = '0 4px 10px rgba(0,0,0,0.1)';
                }}
              >
                <feature.icon size={40} style={{ marginBottom: '15px', color: '#2C7A7B' }} />
                <h3 style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '10px' }}>{feature.title}</h3>
                <p style={{ fontSize: '14px', color: '#4A5568' }}>{feature.description}</p>
              </div>
            ))}
          </div>
        </section>

        {/* Stats Section */}
        <section style={{ padding: '40px', backgroundColor: '#E6F9E9' }}>
          <div style={{ display: 'flex', justifyContent: 'space-around', padding: '20px', borderRadius: '12px' }}>
            {stats.map((stat, index) => (
              <div key={index} style={{ textAlign: 'center' }}>
                <h3 style={{ fontSize: '24px', fontWeight: 'bold', marginBottom: '10px' }}>{stat.value}</h3>
                <p style={{ fontSize: '16px', color: '#4A5568' }}>{stat.label}</p>
              </div>
            ))}
          </div>
        </section>
      </main>
    </div>
  );
};

export default LandingPage;
