import React, { useState } from 'react';
import backgroundImage from '../assets/img/bg2.jpg'; // Correct path to image
import GreenTrendz from '../assets/img/logo_1.png';
import latest1 from '../assets/img/bg4.jpg'; // Correct path to image
import latest2 from '../assets/img/latest.jpg'; // Correct path to image
import latest3 from '../assets/img/latest3.jpg'; // Correct path to image
import { Link } from 'react-router-dom';




import {
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  HelpCircle,
  ArrowRight,

  CloudUpload,
  Trophy,
  Italic,  // Added new icon for benchmarking
} from 'lucide-react';

const styles = {
  landingPage: {
    display: 'flex',
    height: '100vh',
    backgroundColor: '#f4f4f9',
    fontFamily: 'Arial, sans-serif',
  },
  sidebar: {
    width: '250px',
    background: 'linear-gradient(to bottom right, #064e3b, #276749)',
    color: 'white',
    padding: '20px',
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'flex-start',
    position: 'fixed',
    height: '100vh',
    zIndex: 10,
    boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
    transition: 'width 0.3s ease',
  },
  logo: {
    fontSize: '25px',
    marginBottom: '30px',
    fontWeight: 'bold',
    color: '#fff',
    textAlign: 'center',
    marginTop: '20px',
    transition: 'transform 0.3s ease',
  },
  logoHover: {
    transform: 'scale(1.1)',
  },
  navList: {
    listStyle: 'none',
    padding: 0,
    margin: 0,
    width: '100%',
  },
  navItem: {
    color: 'white',
    textDecoration: 'none',
    padding: '15px 20px',
    display: 'flex',
    alignItems: 'center',
    width: '100%',
    borderRadius: '8px',
    marginBottom: '12px',
    transition: 'background-color 0.3s ease, transform 0.3s ease',
    cursor: 'pointer',
    fontfamily: "'Poppins', sans-serif", // Custom font for links
        fontWeight: '500', // Optional for bold text
    },
      navItemHover: {
    backgroundColor: '#3b6b5c',
    transform: 'scale(1.05)',
  },
  navIcon: {
    marginRight: '20px',
    transition: 'transform 0.3s ease',
  },
  content: {
    flex: 1,
    padding: '30px',
    boxSizing: 'border-box',
    backgroundColor: '#fffff',
    overflowY: 'auto',
    marginLeft: '250px',
    transition: 'margin-left 0.3s ease',

  },

  mainSection: {
    padding: '30px',
    backgroundColor: '#f9f9f9',
    borderRadius: '12px',
    boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)',
    marginBottom: '30px',
    transition: 'box-shadow 0.3s ease',
  },
  button: {
    backgroundColor: 'linear-gradient(to right, #38a169, #2f855a)', // Green gradient
    color: 'white',
    padding: '12px 25px',
    borderRadius: '8px',
    border: 'none',
    fontWeight: 'bold',
    display: 'flex',
    alignItems: 'center',
    gap: '10px',
    transition: 'transform 0.3s ease, background-color 0.3s ease, box-shadow 0.3s ease',
    boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
    cursor: 'pointer',
  },
  buttonHover: {
    transform: 'scale(1.05)',
    backgroundColor: 'linear-gradient(to right, #2f855a, #22543d)', // Darker green gradient
    color: 'white',
    boxShadow: '0 6px 15px rgba(0, 0, 0, 0.2)',
  },
  buttonOutline: {
    backgroundColor: 'transparent',
    color: '#2f855a',
    padding: '12px 25px',
    borderRadius: '8px',
    border: '2px solid #2f855a',
    fontWeight: 'bold',
    transition: 'background-color 0.3s ease, transform 0.3s ease, box-shadow 0.3s ease',
  },
  buttonOutlineHover: {
    backgroundColor: '#2f855a',
    color: 'white',
    transform: 'scale(1.05)',
    boxShadow: '0 6px 15px rgba(0, 0, 0, 0.1)',
  },

  header: {    
    fontSize: '50px',    
    fontWeight: 1000, // Use numeric font weight    
    marginBottom: '10px', 
    marginLeft: '90px',   
    fontFamily: "'Roboto', sans-serif", // Make sure to use a font that supports 900 weight    
    color: '#ffffff',

  },
}  

// Updated Features Data
const features = [
  {
    icon: FileText,
    title: 'Automated Reporting',
    description: 'Generate comprehensive ESG reports with just a few clicks. Save time and reduce errors.',
  },
  {
    icon: BarChart2,
    title: 'Data Analytics',
    description: 'Powerful analytics tools to track and visualize your sustainability metrics.',
  },
  {
    icon: Calculator,
    title: 'Carbon Calculator',
    description: 'Accurate carbon footprint calculations based on industry standards.',
  },
  {
    icon: Trophy,
    title: 'Benchmarking',
    description: 'Compare your ESG performance against industry peers and standards.',
  },
];


// Stats Data
const stats = [
  { value: '2M+', label: 'Carbon Data Points' },
  { value: '500+', label: 'Companies' },
  { value: '98%', label: 'Accuracy Rate' },
  { value: '24/7', label: 'Support' },
];

const menuItems = [
  { icon: Home, label: 'Home', path: '/land' },
  { icon: CloudUpload, label: 'Data Upload', path: '/data-upload' },
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
];
const LogoutButton = () => {
  const [showLogoutPopup, setShowLogoutPopup] = useState(false);

  const handleLogout = () => {
    // Redirect to the homepage after logout
    window.location.href = '/';
  };}
const LandingPage = () => {

  return (
    <div style={styles.landingPage}>
      {/* Sidebar */}
      <nav style={styles.sidebar}>
      <div style={{ display: 'flex', alignItems: 'center', }}>
  <img
    src={GreenTrendz}
    alt="Logo"
    width="50"
    height="50"
    className="d-inline-block align-top me-2"
  />
  <h5
    style={{
      ...styles.logo,
      size: '20px',
      margin: 0, // Remove default margin
      onMouseEnter: (e) => (e.target.style.transform = 'scale(1.0)'),
      onMouseLeave: (e) => (e.target.style.transform = ''),
      fontsize: '18px', // Adjust font size here
      fontWeight: 'bold',
    }}
  >
    GreenTrendz
  </h5>
</div>

        <ul style={styles.navList}>
          {menuItems.map((item) => (
            <li key={item.path}>
              <a
                href={item.path}
                style={styles.navItem}
                onMouseEnter={(e) => {
                  e.target.style.backgroundColor = '#3b6b5c'; // Hover effect color
                  e.target.style.transform = 'scale(1.05)';
                }}
                onMouseLeave={(e) => {
                  e.target.style.backgroundColor = '';
                  e.target.style.transform = '';
                }}
              >
                <item.icon style={styles.navIcon} size={20} />
                <span>{item.label}</span>
              </a>
            </li>
          ))}
        </ul>
        {/* Logo and User Info */}
        <div style={{ display: 'flex', flexDirection: 'column', marginTop: 'auto', alignItems: 'center' }}>
          <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
            <img src={GreenTrendz} alt="Company Logo" width="30" height="30" style={{ borderRadius: '50%' }} />
            <span style={{ color: 'white', fontSize: '16px', fontWeight: 'bold' }}>GreenTrendz</span>
          </div>
          <button
            style={styles.button}
            onClick={() => setShowLogoutPopup(true)}
            onMouseEnter={(e) => (e.target.style.backgroundColor = '#276749')}
            onMouseLeave={(e) => (e.target.style.backgroundColor = '#3b6b5c')}
          >
            Logout
          </button>
          </div>
      </nav>

      {/* Main Content */}
      <main style={styles.content}>
  {/* Hero Section */}
  <div
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover',  // Makes sure the image covers the entire div
        backgroundPosition: 'center', // Center the image
        padding: '60px 0',
        minHeight: '350px',
        color: 'white',
        textAlign: 'left',
        borderRadius: '20px',  // Adding rounded corners to the image
        overflow: 'hidden',

 // Ensures content doesn't spill outside the rounded edges
      }}
    >
      <div>
      {/* Google Fonts link */}
      <link
        href="https://fonts.googleapis.com/css2?family=Roboto:wght@100;400;700;900&display=swap"
        rel="stylesheet"
      />
      <h1 style={styles.header}>Transform Your ESG Reporting Process</h1>
    </div>

    <p
  style={{
    fontSize: '18px',
    marginBottom: '20px',
    lineHeight: '1.6', // Improves readability with proper line spacing
    color: '#cffad4', // Soft gray for text color
    marginLeft:'90px',
fontfamily: 'Arial, sans-serif',
marginRight: '30%',
fontStyle:'Italic',
  }}
>
  Generate comprehensive ESG reports, track sustainability metrics, and make data-driven decisions with our powerful platform.
</p>

          <div style={{ display: 'flex', gap: '10px' }}>
          <div style={{ display: 'flex', gap: '10px' }}>
      <Link
        to="/upload"
        style={{
          textDecoration: 'none',
        }}
      >
        <button
          style={{
            backgroundColor: '#fff',
            color: '#276749',
            padding: '10px 20px',
            borderRadius: '5px',
            border: 'none',
            fontWeight: 'bold',
            display: 'flex',
            alignItems: 'center',
            gap: '8px',
            marginLeft: '90px',
            cursor: 'pointer',
            transition: 'all 0.3s ease',
          }}
          onMouseEnter={(e) => {
            e.target.style.backgroundColor = '#276749';
            e.target.style.color = '#fff';
          }}
          onMouseLeave={(e) => {
            e.target.style.backgroundColor = '#fff';
            e.target.style.color = '#276749';
          }}
        >
          Get Started <ArrowRight size={20} />
        </button>
      </Link>
    </div>

<button
  style={{
    backgroundColor: 'transparent',
    color: '#fff',
    padding: '10px 20px',
    borderRadius: '5px',
    border: '1px solid #fff',
    fontWeight: 'bold',
    cursor: 'pointer', // Makes it clickable
    transition: 'all 0.3s ease', // Smooth transition for hover effects

  }}
  onMouseEnter={(e) => {
    e.target.style.backgroundColor = '#fff'; // Hover background color
    e.target.style.color = '#276749'; // Hover text color
  }}
  onMouseLeave={(e) => {
    e.target.style.backgroundColor = 'transparent'; // Reset background color
    e.target.style.color = '#fff'; // Reset text color
  }}
>
  Watch Demo
</button>

          </div>
        </div>


        {/* Features Section */}
<section style={{ padding: '40px', backgroundColor: '#f9f9f9' }}>
  <h2 style={{ fontSize: '30px', fontWeight: 'bold', marginBottom: '30px', textAlign: 'center',color:'green' }}>
    Key Features
  </h2>
  <div style={{ display: 'flex', gap: '20px', justifyContent: 'space-between', flexWrap: 'wrap' }}>
    {features.map((feature, index) => (
      <div
        key={index}
        style={{
          flex: '1 1 calc(25% - 20px)', // Ensures cards are in one row and take equal space
          backgroundColor: '#ffffff',
          padding: '20px',
          borderRadius: '12px',
          boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
          textAlign: 'center',
          transition: 'box-shadow 0.3s ease',
        }}
        onMouseEnter={(e) => {
          e.target.style.boxShadow = '0 6px 20px rgba(0,0,0,0.2)';
        }}
        onMouseLeave={(e) => {
          e.target.style.boxShadow = '0 4px 10px rgba(0,0,0,0.1)';
        }}
      >
        <feature.icon size={40} style={{ marginBottom: '15px', color: '#2C7A7B' }} />
        <h3 style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '10px' }}>{feature.title}</h3>
        <p style={{ fontSize: '14px', color: '#4A5568' }}>{feature.description}</p>
      </div>
    ))}
  </div>
</section>

<section style={{ padding: '40px', backgroundColor: '#f9f9f9' }}>
  {/* Metrics Section */}
  <div
    style={{
      display: 'flex',
      justifyContent: 'space-around',
      padding: '20px',
      backgroundColor: '#E6F9E9',
      borderRadius: '12px',
      marginBottom: '40px',
    }}
  >
    <div style={{ textAlign: 'center' }}>
      <h3 style={{ fontSize: '24px', fontWeight: 'bold', color: '#2C7A7B' }}>2M+</h3>
      <p style={{ margin: 0, fontSize: '14px', color: '#4A5568' }}>Carbon Data Points</p>
    </div>
    <div style={{ textAlign: 'center' }}>
      <h3 style={{ fontSize: '24px', fontWeight: 'bold', color: '#2C7A7B' }}>500+</h3>
      <p style={{ margin: 0, fontSize: '14px', color: '#4A5568' }}>Companies</p>
    </div>
    <div style={{ textAlign: 'center' }}>
      <h3 style={{ fontSize: '24px', fontWeight: 'bold', color: '#2C7A7B' }}>98%</h3>
      <p style={{ margin: 0, fontSize: '14px', color: '#4A5568' }}>Accuracy Rate</p>
    </div>
    <div style={{ textAlign: 'center' }}>
      <h3 style={{ fontSize: '24px', fontWeight: 'bold', color: '#2C7A7B' }}>24/7</h3>
      <p style={{ margin: 0, fontSize: '14px', color: '#4A5568' }}>Support</p>
    </div>
  </div>

  {/* Latest Insights Section */}
  <h2 style={{ fontSize: '30px', fontWeight: 'bold', marginBottom: '30px' }}>
    Latest ESG Insights
  </h2>
  <div style={{ display: 'flex', gap: '20px', justifyContent: 'space-between' }}>
    {/* Insight Card 1 */}
    <div
      style={{
        flex: '1',
        backgroundColor: '#ffffff',
        borderRadius: '12px',
        boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
        overflow: 'hidden',
        transition: 'transform 0.3s ease',
      }}
      onMouseEnter={(e) => {
        e.currentTarget.style.transform = 'scale(1.03)';
      }}
      onMouseLeave={(e) => {
        e.currentTarget.style.transform = 'scale(1)';
      }}
    >
      <img
src={latest1}
       alt="2024 ESG Reporting Trends"
        style={{ width: '100%', height: '150px', objectFit: 'cover' }}
      />
      <div style={{ padding: '15px' }}>
        <h3 style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '10px' }}>
          2024 ESG Reporting Trends
        </h3>
        <p style={{ fontSize: '14px', color: '#4A5568' }}>
          Latest insights on ESG reporting requirements and best practices.
        </p>
      </div>
    </div>

    {/* Insight Card 2 */}
    <div
      style={{
        flex: '1',
        backgroundColor: '#ffffff',
        borderRadius: '12px',
        boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
        overflow: 'hidden',
        transition: 'transform 0.3s ease',
      }}
      onMouseEnter={(e) => {
        e.currentTarget.style.transform = 'scale(1.03)';
      }}
      onMouseLeave={(e) => {
        e.currentTarget.style.transform = 'scale(1)';
      }}
    >
      <img
        src={latest2} // Replace with your image URL
        alt="Carbon Reduction Strategies"
        style={{ width: '100%', height: '150px', objectFit: 'cover' }}
      />
      <div style={{ padding: '15px' }}>
        <h3 style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '10px' }}>
          Carbon Reduction Strategies
        </h3>
        <p style={{ fontSize: '14px', color: '#4A5568' }}>
          Effective strategies for reducing your carbon footprint.
        </p>
      </div>
    </div>

    {/* Insight Card 3 */}
    <div
      style={{
        flex: '1',
        backgroundColor: '#ffffff',
        borderRadius: '12px',
        boxShadow: '0 4px 10px rgba(0,0,0,0.1)',
        overflow: 'hidden',
        transition: 'transform 0.3s ease',
      }}
      onMouseEnter={(e) => {
        e.currentTarget.style.transform = 'scale(1.03)';
      }}
      onMouseLeave={(e) => {
        e.currentTarget.style.transform = 'scale(1)';
      }}
    >
      <img
        src={latest3}// Replace with your image URL
        alt="Tech-Driven Sustainability"
        style={{ width: '100%', height: '150px', objectFit: 'cover' }}
      />
      <div style={{ padding: '15px' }}>
        <h3 style={{ fontSize: '18px', fontWeight: 'bold', marginBottom: '10px' }}>
          Tech-Driven Sustainability
        </h3>
        <p style={{ fontSize: '14px', color: '#4A5568' }}>
          How technology is revolutionizing ESG reporting.
        </p>
      </div>
    </div>
  </div>
</section>


        {/* Stats Section */}

      </main>
    </div>
  );
};

export default LandingPage;
