import React, { useState } from 'react';
import GreenTrendz from '../assets/img/logo_1.png';
import './style.css';  // If style.css is in the same folder as FileUploads.js
import { Info } from 'lucide-react';
import {
    Home,
    LayoutDashboard,
    FileText,
    BarChart2,
    Calculator,
    Settings,
    UserCircle,
    HelpCircle,
    CloudUpload,
} from 'lucide-react';

const styles = {
    landingPage: {
        display: 'flex',
        height: '100vh',
        backgroundColor: '#f4f4f9',
        fontFamily: 'Arial, sans-serif',
    },
    sidebar: {
        width: '250px',
        background: 'linear-gradient(to bottom right, #064e3b, #276749)',
        color: 'white',
        padding: '20px',
        boxSizing: 'border-box',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        position: 'fixed',
        height: '100vh',
        zIndex: 10,
        boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
        transition: 'width 0.3s ease',
    },
    logo: {
        fontSize: '25px',
        marginBottom: '30px',
        fontWeight: 'bold',
        color: '#fff',
        textAlign: 'center',
        marginTop: '20px',
        transition: 'transform 0.3s ease',
    },
    navList: {
        listStyle: 'none',
        padding: 0,
        margin: 0,
        width: '100%',
    },
    navItem: {
        color: 'white',
        textDecoration: 'none',
        padding: '15px 20px',
        display: 'flex',
        alignItems: 'center',
        width: '100%',
        borderRadius: '8px',
        marginBottom: '12px',
        transition: 'background-color 0.3s ease, transform 0.3s ease',
        cursor: 'pointer',
        fontFamily: "'Poppins', sans-serif",
        fontWeight: '500',
    },
    content: {
        flex: 1,
        padding: '30px',
        boxSizing: 'border-box',
        backgroundColor: '#f8f9fa',
        overflowY: 'auto',
        marginLeft: '250px',
        transition: 'margin-left 0.3s ease',
    },
    button: {
        backgroundColor: '#ffffff',
        color: 'Black',
        border: 'none',
        padding: '10px 20px',
        borderRadius: '5px',
        fontWeight: 'bold',
        fontSize: '16px',
        cursor: 'pointer',
        boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
        transition: 'background-color 0.3s ease, transform 0.2s ease',
    },
    infoIcon: {
        marginLeft: '8px',
        cursor: 'pointer',
        fontSize: '6px',
        color: '#666',
        lineHeight: '1',
        width: '20px',
        height: '20px',
        display: 'inline-flex',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: '50%',
        padding: '2px',
    },
    tooltip: {
        position: 'absolute',
        backgroundColor: '#333',
        color: '#fff',
        padding: '5px 10px',
        borderRadius: '4px',
        fontSize: '12px',
        zIndex: '1000',
        visibility: 'hidden',
        opacity: 0,
        transition: 'opacity 0.3s',
    },
    tooltipVisible: {
        visibility: 'visible',
        opacity: 1,
    },
    sectionHeader: {
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        cursor: 'pointer',
        padding: '10px',
        backgroundColor: '#e9e9e9',
        borderRadius: '8px',
    },
    inputContainer: {
        marginBottom: '15px',
    },
    inputField: {
        width: '100%',
        padding: '8px',
        borderRadius: '4px',
        border: '1px solid #ccc',
    },
};

const menuItems = [
    { icon: Home, label: 'Home', path: '/land' },
    { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
    { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
    { icon: FileText, label: 'Reports', path: '/reports' },
    { icon: BarChart2, label: 'Comparison', path: '/comparison' },
    { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
    { icon: Settings, label: 'Settings', path: '/settings' },
    { icon: HelpCircle, label: 'Help & Support', path: '/help' },
    { icon: UserCircle, label: 'Account', path: '/account' },
];

const Sidebar = () => {
    const [showLogoutPopup, setShowLogoutPopup] = useState(false);
    const [tooltipVisible, setTooltipVisible] = useState({});
    const [expandedSections, setExpandedSections] = useState({});

    const showTooltip = (key) => {
        setTooltipVisible((prev) => ({ ...prev, [key]: true }));
    };

    const hideTooltip = (key) => {
        setTooltipVisible((prev) => ({ ...prev, [key]: false }));
    };

    const toggleSection = (section) => {
        setExpandedSections((prev) => ({
            ...prev,
            [section]: !prev[section],
        }));
    };

    const [environmentalData, setEnvironmentalData] = useState({});
    const [socialData, setSocialData] = useState({});
    const [governanceData, setGovernanceData] = useState({});
    const [dataImportData, setDataImportData] = useState({ startDate: '', endDate: '' });
    const [csvFile, setCsvFile] = useState(null);
    const [logoFile, setLogoFile] = useState(null);
    const [loading, setLoading] = useState(false);
    const [successMessage, setSuccessMessage] = useState('');
    const [errorMessage, setErrorMessage] = useState('');

    const handleCsvUpload = (e) => {
        const file = e.target.files[0];
        if (file && file.type !== 'text/csv') {
            alert('Please upload a valid CSV file.');
            return;
        }
        setCsvFile(file);
    };

    const handleLogoUpload = (e) => {
        const file = e.target.files[0];
        if (file && !['image/png', 'image/jpeg'].includes(file.type)) {
            alert('Please upload a valid image file (PNG or JPEG).');
            return;
        }
        setLogoFile(file);
    };

    const handleSubmit = async () => {
        if (!csvFile || !logoFile) {
            alert('Please upload both CSV and Logo files.');
            return;
        }

        setLoading(true);
        setSuccessMessage('');
        setErrorMessage('');

        const formData = new FormData();
        formData.append('environmentalData', JSON.stringify(environmentalData));
        formData.append('socialData', JSON.stringify(socialData));
        formData.append('governanceData', JSON.stringify(governanceData));
        formData.append('startDate', dataImportData.startDate);
        formData.append('endDate', dataImportData.endDate);
        formData.append('csvFile', csvFile);
        formData.append('logoFile', logoFile);

        try {
            const response = await fetch('http://localhost:5003/api/save-esg-data', {
                method: 'POST',
                body: formData,
            });

            if (response.ok) {
                const result = await response.json();
                setSuccessMessage(result.message || 'Data submitted successfully!');
            } else {
                const errorDetail = await response.text();
                throw new Error(`Error ${response.status}: ${errorDetail}`);
            }
        } catch (error) {
            setErrorMessage('Error submitting data: ' + error.message);
        } finally {
            setLoading(false);
        }
    };

    const environmentalSections = [
        {
            title: 'Energy',
            metrics: [
                { label: 'Coal (tons, GJ)', key: 'coal', type: 'number' },
                { label: 'Diesel (L, GJ)', key: 'diesel', type: 'number' },
                { label: 'High-Speed Diesel (HSD) (L, GJ)', key: 'hsd', type: 'number' },
                { label: 'Petrol (L, GJ)', key: 'petrol', type: 'number' },
                { label: 'Liquefied Petroleum Gas (LPG) (m³, GJ)', key: 'lpg', type: 'number' },
                { label: 'Natural Gas (m³, GJ)', key: 'naturalGas', type: 'number' },
                { label: 'LNG/PNG (m³, GJ)', key: 'lngPng', type: 'number' },
                { label: 'Solar Energy (MWh, kWh)', key: 'solarEnergy', type: 'number' },
                { label: 'Wind Energy (MWh, kWh)', key: 'windEnergy', type: 'number' },
                { label: 'Hydroelectric Energy (MWh, kWh)', key: 'hydroEnergy', type: 'number' },
                { label: 'Biomass (tons, GJ)', key: 'biomass', type: 'number' },
                { label: 'Renewable Energy Generated (MWh, %)', key: 'renewableEnergyGenerated', type: 'number' },
                { label: 'On-site Energy Consumption (MWh, GJ)', key: 'onsiteEnergyConsumption', type: 'number' },
                { label: 'Biodiesel (L, GJ)', key: 'biodiesel', type: 'number' },
            ],
        },
        {
            title: 'Water',
            metrics: [
                { label: 'Produced Water Withdrawal (m³, L)', key: 'producedWaterWithdrawal', type: 'number' },
                { label: 'Surface Water Withdrawal (m³, L)', key: 'surfaceWaterWithdrawal', type: 'number' },
                { label: 'Ground Water Withdrawal (m³, L)', key: 'groundWaterWithdrawal', type: 'number' },
                { label: 'Seawater Withdrawal (m³, L)', key: 'seawaterWithdrawal', type: 'number' },
                { label: 'Third-Party Water Withdrawal (m³, L)', key: 'thirdPartyWaterWithdrawal', type: 'number' },
                { label: 'Water Consumption (m³, L)', key: 'waterConsumption', type: 'number' },
                { label: 'Water Recycled (m³, %)', key: 'waterRecycled', type: 'number' },
                { label: 'Water Discharge to Surface Water (m³, L)', key: 'waterDischargeSurface', type: 'number' },
                { label: 'Water Discharge to Groundwater (m³, L)', key: 'waterDischargeGround', type: 'number' },
                { label: 'Water Discharge to Third-Party (m³, L)', key: 'waterDischargeThirdParty', type: 'number' },
                { label: 'Water Discharge to Seawater (m³, L)', key: 'waterDischargeSeawater', type: 'number' },
                { label: 'Wastewater Generated (m³, L)', key: 'wastewaterGenerated', type: 'number' },
            ],
        },
        {
            title: 'Waste',
            metrics: [
                { label: 'E-Waste (kg, tons, % recycled)', key: 'eWaste', type: 'number' },
                { label: 'Plastic Waste (kg, tons, % recycled)', key: 'plasticWaste', type: 'number' },
                { label: 'Metal Waste (kg, tons, % recycled)', key: 'metalWaste', type: 'number' },
                { label: 'Glass Waste (kg, tons, % recycled)', key: 'glassWaste', type: 'number' },
                { label: 'Batteries and Accumulator Waste (kg, tons, % recycled)', key: 'batteryWaste', type: 'number' },
                { label: 'Civil Waste (kg, tons, % recycled)', key: 'civilWaste', type: 'number' },
                { label: 'Miscellaneous Waste (kg, tons, % recycled)', key: 'miscWaste', type: 'number' },
                { label: 'Biomedical Waste (kg, tons, % recycled)', key: 'biomedicalWaste', type: 'number' },
                { label: 'Feed Waste (kg, tons, % recycled)', key: 'feedWaste', type: 'number' },
                { label: 'Hazardous Waste Generation (kg, tons)', key: 'hazardousWaste', type: 'number' },
            ],
        },
        {
            title: 'Emissions',
            metrics: [
                { label: 'Greenhouse Gas (GHG) Emissions (tons CO₂e)', key: 'ghgEmissions', type: 'number' },
                { label: 'Effluent Discharge (m³, L, ppm)', key: 'effluentDischarge', type: 'number' },
            ],
        },
        {
            title: 'Other Environmental Factors',
            metrics: [
                { label: 'Environmental Incidents (count)', key: 'environmentalIncidents', type: 'number' },
                { label: 'Solar Panel Recycling (% recycled, kg, tons)', key: 'solarPanelRecycling', type: 'number' },
            ],
        },
    ];
    const socialSections = [
        {
            title: 'Workforce Management',
            metrics: [
                { label: 'Employee Diversity & Inclusion (% female, % minorities, % disabled employees)', key: 'employeeDiversity', type: 'number' },
                { label: 'Employee Training & Development (hours per employee, $ spent per employee)', key: 'employeeTraining', type: 'number' },
                { label: 'Employee Turnover Rate (%)', key: 'employeeTurnover', type: 'number' },
                { label: 'Workplace Safety & Health (injury rate per 100 employees, fatalities count)', key: 'workplaceSafety', type: 'number' },
                { label: 'Human Rights & Labor Standards (% compliance, number of reported violations)', key: 'humanRights', type: 'number' },
                { label: 'Fair Wages & Compensation (minimum wage compliance %, wage gap %)', key: 'fairWages', type: 'number' },
            ],
        },
        {
            title: 'Community Engagement',
            metrics: [
                { label: 'Corporate Social Responsibility (CSR) Programs (number of programs, $ spent)', key: 'csrPrograms', type: 'number' },
                { label: 'Community Investments (total $ invested)', key: 'communityInvestments', type: 'number' },
                { label: 'Volunteer Programs (% employee participation, hours volunteered)', key: 'volunteerPrograms', type: 'number' },
                { label: 'Philanthropic Activities ($ donated, number of beneficiaries)', key: 'philanthropicActivities', type: 'number' },
            ],
        },
        {
            title: 'Product Responsibility',
            metrics: [
                { label: 'Customer Health & Safety (% compliance with safety standards, number of recalls)', key: 'customerSafety', type: 'number' },
                { label: 'Data Privacy & Protection (number of breaches, % compliance with GDPR/CCPA)', key: 'dataPrivacy', type: 'number' },
                { label: 'Ethical Marketing & Advertising (% compliance, number of violations)', key: 'ethicalMarketing', type: 'number' },
                { label: 'Product Quality & Safety (number of defects per million units, recall count)', key: 'productQuality', type: 'number' },
            ],
        },
        {
            title: 'Supply Chain Responsibility',
            metrics: [
                { label: 'Supplier ESG Compliance (% compliant suppliers, count of audits conducted)', key: 'supplierCompliance', type: 'number' },
                { label: 'Fair Trade Practices (% fair-trade certified suppliers)', key: 'fairTrade', type: 'number' },
                { label: 'Ethical Sourcing (% responsibly sourced materials)', key: 'ethicalSourcing', type: 'number' },
            ],
        },
    ];

    return (
        <div style={styles.landingPage}>
            <nav style={styles.sidebar}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <img
                        src={GreenTrendz}
                        alt="Logo"
                        width="50"
                        height="50"
                        className="d-inline-block align-top me-2"
                    />
                    <h5 style={styles.logo}>GreenTrendz</h5>
                </div>
                <ul style={styles.navList}>
                    {menuItems.map((item) => (
                        <li key={item.path}>
                            <a
                                href={item.path}
                                style={styles.navItem}
                                onMouseEnter={(e) => {
                                    e.target.style.backgroundColor = '#3b6b5c';
                                    e.target.style.transform = 'scale(1.05)';
                                }}
                                onMouseLeave={(e) => {
                                    e.target.style.backgroundColor = '';
                                    e.target.style.transform = '';
                                }}
                            >
                                <item.icon style={styles.navIcon} size={20} />
                                <span>{item.label}</span>
                            </a>
                        </li>
                    ))}
                </ul>
                <div style={{ marginTop: 'auto', textAlign: 'center' }}>
                    <button
                        style={styles.button}
                        onClick={() => setShowLogoutPopup(true)}
                    >
                        Logout
                    </button>
                </div>
            </nav>
            <div style={styles.content}>
                <div className="container">
                    <div className="section">
                        <h1 className="sectionTitle">Upload ESG Data</h1>
                        <h2 className="sectionTitle">Annual ESG Metrics</h2>

                          {/* Environmental Section */}
                    <div className="section">
                        <h3>Environmental</h3>
                        {environmentalSections.map((section, idx) => (
                            <div key={idx} style={styles.section}>
                                <div
                                    style={styles.sectionHeader}
                                    onClick={() => toggleSection(section.title)}
                                >
                                    <h4>{section.title}</h4>
                                    <span>{expandedSections[section.title] ? '▲' : '▼'}</span>
                                </div>
                                {expandedSections[section.title] && (
                                    <div>
                                        {section.metrics.map((metric, i) => (
                                            <div key={i} style={styles.inputContainer}>
                                                <label htmlFor={metric.key}>
                                                    {metric.label}
                                                    <Info
                                                        style={styles.infoIcon}
                                                        onMouseEnter={() => showTooltip(metric.key)}
                                                        onMouseLeave={() => hideTooltip(metric.key)}
                                                    />
                                                </label>
                                                <input
                                                    id={metric.key}
                                                    type={metric.type}
                                                    value={environmentalData[metric.key] || ''}
                                                    onChange={(e) =>
                                                        setEnvironmentalData((prev) => ({
                                                            ...prev,
                                                            [metric.key]: e.target.value,
                                                        }))
                                                    }
                                                    style={styles.inputField}
                                                />
                                            </div>
                                        ))}
                                    </div>
                                )}
                            </div>
                        ))}
                        <button
                            style={styles.button}
                            onClick={() => alert('Add New Metric functionality here')}
                        >
                            Add New Environmental Metric
                        </button>
                    </div>

                    {/* Social Section */}
                    <div className="section">
                        <h3>Social</h3>
                        {/* Add Social Metrics Here */}
                    </div>

                    {/* Governance Section */}
                    <div className="section">
                        <h3>Governance</h3>
                        {/* Add Governance Metrics Here */}
                    </div>
                            <button 
                            className="button"
                            onClick={(e) => {
                                const rect = e.target.getBoundingClientRect();
                                setModalPosition({
                                    x: rect.left,
                                    y: rect.top + window.scrollY
                                });
                                setNewMetricForm(prev => ({ ...prev, section: 'Governance' }));
                                setShowNewMetricForm(true);
                            }}
                                    >
                                        Add New Governance Metric
                                    </button>
                        </div>
                    </div>




{/* Data Import Section */}
<div className="section">
  <h3>Data Import</h3>

  <div className="date-picker-container">
    <div className="inputColumn">
      <label>Start Date</label>
      <input
        type="date"
        value={dataImportData.startDate}
        onChange={(e) =>
          setDataImportData({
            ...dataImportData,
            startDate: e.target.value,
          })
        }
        className="inputField"
      />
    </div>
    <div className="inputColumn">
      <label>End Date</label>
      <input
        type="date"
        value={dataImportData.endDate}
        onChange={(e) =>
          setDataImportData({
            ...dataImportData,
            endDate: e.target.value,
          })
        }
        className="inputField"
      />
    </div>
  </div>
</div>



           {/* File Upload Section */}
<div className="section">
    <h3>Upload CSV</h3>
    <div className="file-upload-container">
        <input
            type="file"
            accept=".csv"
            onChange={handleCsvUpload}
            className="inputField"
            id="csv-upload"
        />
        <label htmlFor="csv-upload" className="upload-label">
            <p>Click to upload or drag and drop</p>
            <p>Upload a CSV file (max. 5MB) containing Metric Name, Value, and Timestamp columns</p>
        </label>
    </div>
    {csvFile && (
        <div className="preview">
            <p>{csvFile.name}</p>
            <p>{Math.round(csvFile.size / 1024)} KB</p>
        </div>
    )}
</div>

{/* Logo Upload Section */}
<div className="section">
    <h3>Upload Logo</h3>
    <div className="file-upload-container">
        <input
            type="file"
            accept="image/*"
            onChange={handleLogoUpload}
            className="inputField"
            id="logo-upload"
        />
        <label htmlFor="logo-upload" className="upload-label">
            <p>Click to upload or drag and drop</p>
            <p>Upload your company logo (JPEG/PNG/SVG, max. 2MB)</p>
        </label>
    </div>
    {logoFile && (
        <div className="preview">
            <img src={logoFile} alt="Logo" width="100" />
        </div>
    )}
</div>

{/* Submit Button */}
<button className="button" onClick={handleSubmit}>
    Submit Data
</button>
<div>
        {loading && <p>Submitting data, please wait...</p>}
        {successMessage && <p className="success">{successMessage}</p>}
        {errorMessage && <p className="error">{errorMessage}</p>}
    </div>
</div>
    </div>

    );
};

export default Sidebar;
