import React, { useState } from 'react';
import { 
  Home,
  LayoutDashboard,
  FileText,
  BarChart2,
  Calculator,
  Settings,
  HelpCircle
} from 'lucide-react';
import { useLocation } from 'react-router-dom';

const menuItems = [
  { icon: Home, label: 'Home', path: '/' },
  { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
  { icon: FileText, label: 'Reports', path: '/reports' },
  { icon: BarChart2, label: 'Comparison', path: '/comparison' },
  { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
  { icon: Settings, label: 'Settings', path: '/settings' },
  { icon: HelpCircle, label: 'Help & Support', path: '/help' },
];

const Sidebar = () => {
  const location = useLocation();
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);

  const handleToggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <aside className={`bg-emerald-900 text-white w-64 min-h-screen p-4 ${isSidebarOpen ? 'block' : 'hidden'} md:block`}>
      <div className="flex items-center mb-8 px-2">
        <FileText className="h-8 w-8 text-emerald-400" />
        <span className="text-xl font-bold ml-2">ESG Reporter</span>
      </div>

      <button onClick={handleToggleSidebar} className="text-white md:hidden p-2 rounded-md">
        Toggle Sidebar
      </button>

      <nav>
        {menuItems.map((item) => (
          <a
            key={item.path}
            href={item.path}
            className={`flex items-center px-4 py-3 text-gray-300 hover:bg-emerald-800 rounded-lg mb-1 transition-colors ${location.pathname === item.path ? 'bg-emerald-700' : ''}`}
            aria-label={item.label}
          >
            <item.icon className="h-5 w-5 mr-3" />
            <span>{item.label}</span>
          </a>
        ))}
      </nav>
    </aside>
  );
};

export default Sidebar;
