import React, { useState } from 'react';
import GreenTrendz from '../assets/img/logo_1.png';
import './style.css';  // If style.css is in the same folder as FileUploads.js
import { Info } from 'lucide-react';
import {
    Home,
    LayoutDashboard,
    FileText,
    BarChart2,
    Calculator,
    Settings,
    UserCircle,
    HelpCircle,
    CloudUpload,
} from 'lucide-react';

const styles = {
    landingPage: {
        display: 'flex',
        height: '100vh',
        backgroundColor: '#f4f4f9',
        fontFamily: 'Arial, sans-serif',
    },
    sidebar: {
        width: '250px',
        background: 'linear-gradient(to bottom right, #064e3b, #276749)',
        color: 'white',
        padding: '20px',
        boxSizing: 'border-box',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        position: 'fixed',
        height: '100vh',
        zIndex: 10,
        boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
        transition: 'width 0.3s ease',
    },
    logo: {
        fontSize: '25px',
        marginBottom: '30px',
        fontWeight: 'bold',
        color: '#fff',
        textAlign: 'center',
        marginTop: '20px',
        transition: 'transform 0.3s ease',
    },
    navList: {
        listStyle: 'none',
        padding: 0,
        margin: 0,
        width: '100%',
    },
    navItem: {
        color: 'white',
        textDecoration: 'none',
        padding: '15px 20px',
        display: 'flex',
        alignItems: 'center',
        width: '100%',
        borderRadius: '8px',
        marginBottom: '12px',
        transition: 'background-color 0.3s ease, transform 0.3s ease',
        cursor: 'pointer',
        fontFamily: "'Poppins', sans-serif",
        fontWeight: '500',
    },
    content: {
        flex: 1,
        padding: '30px',
        boxSizing: 'border-box',
        backgroundColor: '#f8f9fa',
        overflowY: 'auto',
        marginLeft: '250px',
        transition: 'margin-left 0.3s ease',
    },
    button: {
        backgroundColor: '#ffffff',
        color: 'Black',
        border: 'none',
        padding: '10px 20px',
        borderRadius: '5px',
        fontWeight: 'bold',
        fontSize: '16px',
        cursor: 'pointer',
        boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)',
        transition: 'background-color 0.3s ease, transform 0.2s ease',
    },
    infoIcon: {
        marginLeft: '8px',
        cursor: 'pointer',
        fontSize: '6px',
        color: '#666',
        lineHeight: '1',
        width: '20px',
        height: '20px',
        display: 'inline-flex',
        alignItems: 'center',
        justifyContent: 'center',
        borderRadius: '50%',
        padding: '2px',
    },
    tooltip: {
