import React, { useState } from 'react';
import GreenTrendz from '../assets/img/logo_1.png';
import './style.css';  // If style.css is in the same folder as FileUploads.js


import {
    Home,
    LayoutDashboard,
    FileText,
    BarChart2,
    Calculator,
    Settings,
    UserCircle,
    HelpCircle,
    CloudUpload,
} from 'lucide-react';

const styles = {
    landingPage: {
        display: 'flex',
        height: '100vh',
        backgroundColor: '#f4f4f9',
        fontFamily: 'Arial, sans-serif',
      },
      sidebar: {
        width: '250px',
        background: 'linear-gradient(to bottom right, #064e3b, #276749)',
        color: 'white',
        padding: '20px',
        boxSizing: 'border-box',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'flex-start',
        position: 'fixed',
        height: '100vh',
        zIndex: 10,
        boxShadow: '2px 0 15px rgba(0, 0, 0, 0.15)',
        transition: 'width 0.3s ease',
      },
      logo: {
        fontSize: '25px',
        marginBottom: '30px',
        fontWeight: 'bold',
        color: '#fff',
        textAlign: 'center',
        marginTop: '20px',
        transition: 'transform 0.3s ease',
      },
      logoHover: {
        transform: 'scale(1.1)',
      },
      navList: {
        listStyle: 'none',
        padding: 0,
        margin: 0,
        width: '100%',
      },
      navItem: {
        color: 'white',
        textDecoration: 'none',
        padding: '15px 20px',
        display: 'flex',
        alignItems: 'center',
        width: '100%',
        borderRadius: '8px',
        marginBottom: '12px',
        transition: 'background-color 0.3s ease, transform 0.3s ease',
        cursor: 'pointer',
        fontfamily: "'Poppins', sans-serif", // Custom font for links
            fontWeight: '500', // Optional for bold text
        },
          navItemHover: {
        backgroundColor: '#3b6b5c',
        transform: 'scale(1.05)',
      },
      navIcon: {
        marginRight: '20px',
        transition: 'transform 0.3s ease',
      },

        content: {
            flex: 1,
            padding: '30px',
            boxSizing: 'border-box',
            backgroundColor: '#f8f9fa', // Light gray background for a softer look
            overflowY: 'auto',
            marginLeft: '250px',
            transition: 'margin-left 0.3s ease',
        },





    button: {
        backgroundColor: '#ffffff', // Default background color
        color: 'Black', // Text color
        border: 'none', // No border
        padding: '10px 20px', // Padding for a nice clickable area
        borderRadius: '5px', // Rounded corners
        fontWeight: 'bold', // Bold text
        fontSize: '16px', // Font size
        cursor: 'pointer', // Pointer cursor on hover
        boxShadow: '0px 4px 6px rgba(0, 0, 0, 0.1)', // Subtle shadow for a 3D effect
        transition: 'background-color 0.3s ease, transform 0.2s ease', // Smooth transition for hover effects
      },
};

const menuItems = [
    { icon: Home, label: 'Home', path: '/land' },
    { icon: CloudUpload, label: 'Data Upload', path: '/upload' },
    { icon: LayoutDashboard, label: 'Dashboard', path: '/dashboard' },
    { icon: FileText, label: 'Reports', path: '/reports' },
    { icon: BarChart2, label: 'Comparison', path: '/comparison' },
    { icon: Calculator, label: 'Carbon Calculator', path: '/calculator' },
    { icon: Settings, label: 'Settings', path: '/settings' },
    { icon: HelpCircle, label: 'Help & Support', path: '/help' },
    { icon: UserCircle, label: 'Account', path: '/account' }, // Added Account item

];

const ESGDataForm = () => {
    const initialEnvironmentalData = {
        totalEnergyConsumed: '',
        renewableEnergyUsage: '',
        dataCenterPUE: '',
        scope1Emissions: '',
        scope2Emissions: '',
        scope3Emissions: '',
        carbonOffsets: '',
        waterConsumption: '',
        waterRecycled: '',
        eWasteGenerated: '',
        eWasteRecycled: '',
        recyclabilityPercentage: '',
        ethicalSourcingPercentage: '',
        sustainabilityCertifications: '',
        renewableEnergyCertificates: ''
    };

    const initialSocialData = {
        employeeDiversity: '',
        employeeEngagement: '',
        employeeTurnover: '',
        communityInitiatives: ''
    };

    const initialGovernanceData = {
        boardDiversity: '',
        cybersecurityIncidents: '',
        complianceViolations: '',
        ceoPayRatio: ''
    };

    const [environmentalData, setEnvironmentalData] = useState(initialEnvironmentalData);
    const [socialData, setSocialData] = useState(initialSocialData);
    const [governanceData, setGovernanceData] = useState(initialGovernanceData);
    const [dateRange, setDateRange] = useState({ startDate: '', endDate: '' });
    const [files, setFiles] = useState({ csvFile: null, logoFile: null });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState(null);
    const [success, setSuccess] = useState(false);

    const handleEnvironmentalChange = (e) => {
        const { name, value } = e.target;
        setEnvironmentalData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSocialChange = (e) => {
        const { name, value } = e.target;
        setSocialData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleGovernanceChange = (e) => {
        const { name, value } = e.target;
        setGovernanceData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleDateChange = (e) => {
        const { name, value } = e.target;
        setDateRange(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleFileChange = (e) => {
        const { name, files } = e.target;
        setFiles(prev => ({
            ...prev,
            [name]: files[0]
        }));
    };

    const validateData = () => {
        // Add your validation logic here
        if (!dateRange.startDate || !dateRange.endDate) {
            throw new Error('Please select both start and end dates');
        }
        if (new Date(dateRange.endDate) <= new Date(dateRange.startDate)) {
            throw new Error('End date must be after start date');
        }
        // Add more validation as needed
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError(null);
        setSuccess(false);

        try {
            validateData();

            const formData = new FormData();
            formData.append('environmentalData', JSON.stringify(environmentalData));
            formData.append('socialData', JSON.stringify(socialData));
            formData.append('governanceData', JSON.stringify(governanceData));
            formData.append('startDate', dateRange.startDate);
            formData.append('endDate', dateRange.endDate);

            if (files.csvFile) formData.append('csvFile', files.csvFile);
            if (files.logoFile) formData.append('logoFile', files.logoFile);

            const response = await axios.post('http://localhost:5000/api/save-esg-data', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                }
            });

            setSuccess(true);
            // Reset form
            setEnvironmentalData(initialEnvironmentalData);
            setSocialData(initialSocialData);
            setGovernanceData(initialGovernanceData);
            setDateRange({ startDate: '', endDate: '' });
            setFiles({ csvFile: null, logoFile: null });
        } catch (err) {
            setError(err.message || 'An error occurred while saving the data');
        } finally {
            setLoading(false);
        }
    };

    return (

        <div style={styles.landingPage}>
            <nav style={styles.sidebar}>
                <div style={{ display: 'flex', alignItems: 'center' }}>
                    <img
                        src={GreenTrendz}
                        alt="Logo"
                        width="50"
                        height="50"
                        className="d-inline-block align-top me-2"
                    />
                    <h5 style={styles.logo}>GreenTrendz</h5>
                </div>
                <ul style={styles.navList}>
                    {menuItems.map((item) => (
                        <li key={item.path}>
                            <a
                                href={item.path}
                                style={styles.navItem}
                                onMouseEnter={(e) => {
                                    e.target.style.backgroundColor = '#3b6b5c';
                                    e.target.style.transform = 'scale(1.05)';
                                }}
                                onMouseLeave={(e) => {
                                    e.target.style.backgroundColor = '';
                                    e.target.style.transform = '';
                                }}
                            >
                                <item.icon style={styles.navIcon} size={20} />
                                <span>{item.label}</span>
                            </a>
                        </li>
                    ))}
                </ul>
                <div style={{ marginTop: 'auto', textAlign: 'center' }}>
                    <button
                        style={styles.button}
                        onClick={() => setShowLogoutPopup(true)}
                    >
                        Logout
                    </button>
                </div>
            </nav>
            <div style={styles.content}>
            <div className="container">
    <div className="section">
        <h1 className="sectionTitle">Upload ESG Data</h1>

            <h2 className="sectionTitle">Annual ESG Metrics</h2>

            {/* Environmental Section */}
<div className="section">
    <h3>Environmental</h3>
    <div className="inputContainer">
        {[
            // Energy Consumption
            'Total Energy Consumed (kWh)',
            'Renewable Energy Usage (%)',
            'Data Center Power Usage Effectiveness (PUE)',
            // Carbon Emissions
            'Scope 1 Emissions (metric tons CO₂e)',
            'Scope 2 Emissions (metric tons CO₂e)',
            'Scope 3 Emissions (metric tons CO₂e)',
            'Carbon Offset Projects and Reductions Achieved (metric tons CO₂e)',
            // Water Usage
            'Water Consumption (liters/year)',
            'Percentage of Water Recycled (%)',
            // Electronic Waste (E-Waste)
            'Volume of E-Waste Generated (tons/year)',
            'E-Waste Recycled (%)',
            // Sustainable Product Design
            'Percentage of Products Designed for Recyclability or Repairability',
            'Ethical Sourcing of Materials (% of total)',
            // Sustainability Certifications
            'List of Sustainability Certifications (e.g., ISO 14001)',
            'Renewable Energy Certificates Purchased',
        ].map((label, idx) => {
            const keys = [
                'totalEnergyConsumed',
                'renewableEnergyUsage',
                'dataCenterPUE',
                'scope1Emissions',
                'scope2Emissions',
                'scope3Emissions',
                'carbonOffsets',
                'waterConsumption',
                'waterRecycled',
                'eWasteGenerated',
                'eWasteRecycled',
                'recyclabilityPercentage',
                'ethicalSourcingPercentage',
                'sustainabilityCertifications',
                'renewableEnergyCertificates',
            ];
            return (
                <div key={idx}>
                    <label>{label}</label>
                    {keys[idx] === 'sustainabilityCertifications' ||
                    keys[idx] === 'renewableEnergyCertificates' ? (
                        <input
                            type="text"
                            value={environmentalData[keys[idx]]}
                            onChange={(e) =>
                                setEnvironmentalData({
                                    ...environmentalData,
                                    [keys[idx]]: e.target.value,
                                })
                            }
                            className="inputField"
                        />
                    ) : (
                        <input
                            type="number"
                            value={environmentalData[keys[idx]]}
                            onChange={(e) =>
                                setEnvironmentalData({
                                    ...environmentalData,
                                    [keys[idx]]: e.target.value,
                                })
                            }
                            className="inputField"
                        />
                    )}
                </div>
            );
        })}
    </div>
</div>

{/* Social Section */}
<div className="section">
    <h3>Social</h3>
    <div className="inputContainer">
        {[
            'Employee Diversity (%)',
            'Employee Engagement Score',
            'Employee Turnover Rate (%)',
            'Community Initiatives Investment ($)',
            'Employee Satisfaction Score',
            'Training Hours per Employee (hours)',
            'Workplace Incidents Reported',
            'Gender Pay Gap (%)',
        ].map((label, idx) => {
            const keys = [
                'employeeDiversity',
                'employeeEngagement',
                'employeeTurnover',
                'communityInitiatives',
                'employeeSatisfaction',
                'trainingHours',
                'workplaceIncidents',
                'genderPayGap',
            ];
            return (
                <div key={idx}>
                    <label>{label}</label>
                    <input
                        type="number"
                        value={socialData[keys[idx]]}
                        onChange={(e) =>
                            setSocialData({
                                ...socialData,
                                [keys[idx]]: e.target.value,
                            })
                        }
                        className="inputField"
                    />
                </div>
            );
        })}
    </div>
</div>

{/* Governance Section */}
<div className="section">
    <h3>Governance</h3>
    <div className="inputContainer">
        {[
            'Board Diversity (%)',
            'Cybersecurity Incidents Reported',
            'Compliance Violations',
            'CEO Pay Ratio',
            'Independent Directors (%)',
            'ESG Oversight Budget ($)',
            'Number of Whistleblower Reports',
            'Stakeholder Engagement Meetings',
        ].map((label, idx) => {
            const keys = [
                'boardDiversity',
                'cybersecurityIncidents',
                'complianceViolations',
                'ceoPayRatio',
                'independentDirectors',
                'esgOversightBudget',
                'whistleblowerReports',
                'stakeholderMeetings',
            ];
            return (
                <div key={idx}>
                    <label>{label}</label>
                    <input
                        type="number"
                        value={governanceData[keys[idx]]}
                        onChange={(e) =>
                            setGovernanceData({
                                ...governanceData,
                                [keys[idx]]: e.target.value,
                            })
                        }
                        className="inputField"
                    />
                </div>
            );
        })}
    </div>
</div>

{/* Data Import Section */}
<div className="section">
  <h3>Data Import</h3>

  <div className="date-picker-container">
    <div className="inputColumn">
      <label>Start Date</label>
      <input
        type="date"
        value={dataImportData.startDate}
        onChange={(e) =>
          setDataImportData({
            ...dataImportData,
            startDate: e.target.value,
          })
        }
        className="inputField"
      />
    </div>
    <div className="inputColumn">
      <label>End Date</label>
      <input
        type="date"
        value={dataImportData.endDate}
        onChange={(e) =>
          setDataImportData({
            ...dataImportData,
            endDate: e.target.value,
          })
        }
        className="inputField"
      />
    </div>
  </div>
</div>



           {/* File Upload Section */}
<div className="section">
    <h3>Upload CSV</h3>
    <div className="file-upload-container">
        <input
            type="file"
            accept=".csv"
            onChange={handleCsvUpload}
            className="inputField"
            id="csv-upload"
        />
        <label htmlFor="csv-upload" className="upload-label">
            <p>Click to upload or drag and drop</p>
            <p>Upload a CSV file (max. 5MB) containing Metric Name, Value, and Timestamp columns</p>
        </label>
    </div>
    {csvFile && (
        <div className="preview">
            <p>{csvFile.name}</p>
            <p>{Math.round(csvFile.size / 1024)} KB</p>
        </div>
    )}
</div>

{/* Logo Upload Section */}
<div className="section">
    <h3>Upload Logo</h3>
    <div className="file-upload-container">
        <input
            type="file"
            accept="image/*"
            onChange={handleLogoUpload}
            className="inputField"
            id="logo-upload"
        />
        <label htmlFor="logo-upload" className="upload-label">
            <p>Click to upload or drag and drop</p>
            <p>Upload your company logo (JPEG/PNG/SVG, max. 2MB)</p>
        </label>
    </div>
    {logoFile && (
        <div className="preview">
            <img src={logoFile} alt="Logo" width="100" />
        </div>
    )}
</div>

{/* Submit Button */}
<button className="button" onClick={handleSubmit}>
    Submit Data
</button>
</div>
    </div>
</div>

</div>
    );
};

export default Sidebar;
